"use strict";(self["webpackChunknotes_messanger"]=self["webpackChunknotes_messanger"]||[]).push([[865],{2865:function(n,i,s){s.r(i),s.d(i,{default:function(){return t}});var o=function(){var n=this,i=n._self._c;return n.champion?i("div",[i("div",{staticClass:"contenttext"},[n._v(n._s(n.champion.name))]),i("div",{staticClass:"contentext"},[n._v(n._s(n.champion.description))]),i("div",{staticClass:"contentext"},[n._v("Популяность = "+n._s(n.champion.info.popular))]),i("div",{staticClass:"contentext"},[n._v("Винрейт = "+n._s(n.champion.info.win))]),i("div",{staticClass:"contentext"},[n._v("Блокировки = "+n._s(n.champion.info.block))]),i("div",{staticClass:"contentext"},[n._v("Пассивное : "+n._s(n.champion.info.skills.passive))]),i("div",{staticClass:"contentext"},[n._v(n._s(n.champion.info.skills.Q))]),i("div",{staticClass:"contentext"},[n._v(n._s(n.champion.info.skills.W))]),i("div",{staticClass:"contentext"},[n._v(n._s(n.champion.info.skills.E))]),i("div",{staticClass:"contentext"},[n._v(n._s(n.champion.info.skills.R))])]):n._e()},l=[],p={props:{},components:{},data(){return{champion:null,champions:[{name:"Зери",description:"АДК",info:{popular:"8,5%",win:"50,6%",block:"3,6%",skills:{passive:"ХОДЯЧАЯ БАТАРЕЙКА\nЗери ускоряется, когда получает щит. Нанося урон врагу со щитом, она поглощает его энергию и забирает щит себе.\n\n",Q:"[Q]\nСТРЕЛЬБА ОЧЕРЕДЬЮ\nАвтоатаки Зери считаются умениями, а Стрельба очередью считается автоатакой. Передвигаясь и применяя Стрельбу очередью, Зери заряжает энергией электроранец. Когда уровень заряда достигает максимума, ее следующая автоатака замедляет цель и наносит дополнительный урон. Во время Стрельбы очередью Зери выпускает 7 снарядов, которые наносят урон автоатаки первому пораженному врагу.\n\n",W:"[W]\nИМПУЛЬСНЫЙ РАЗРЯД\nЗери выпускает заряд электричества, который замедляет первого пораженного врага и наносит ему урон. Если заряд попадает в стену, из этого места исходит дальнобойный лазерный луч.\n\n",E:"[E]\nШАЛЬНАЯ ИСКРА\nЗери совершает рывок на небольшое расстояние и усиливает 3 следующих применения Стрельбы очередью, из-за чего снаряды проходят сквозь врагов. Если Зери сталкивается с любым элементом ландшафта, она перелетает его.\n\n",R:"[R]\nКОРОТКОЕ ЗАМЫКАНИЕ\nЗери выбрасывает энергию и перегружает себя. Во время Перегрузки ее урон и скорость передвижения увеличены. Каждый раз, когда Зери поражает вражеского чемпиона, продолжительность и сила ускорения увеличиваются. Пока действует умение, весь урон Стрельбы очередью концентрируется в трех снарядах; при попадании по врагу появляется цепная молния, которая поражает дополнительные цели.\n\n"}}},{name:"Рената Гласк",description:"Сапорт",info:{popular:"4,3%",win:"51,4%",block:"3,1%",skills:{passive:"РЫЧАГ ДАВЛЕНИЯ\nАвтоатаки Ренаты наносят дополнительный урон и помечают врагов. Когда союзники наносят урон помеченным врагам, те получают дополнительный урон.\n\n",Q:"[Q]\nРУКОПОЖАТИЕ\nРената выпускает снаряд, который обездвиживает первого пораженного врага. При повторном применении она отбрасывает цель в выбранном направлении.\n\n",W:"[W]\nАНТИКРИЗИСНЫЕ МЕРЫ\nРената усиливает союзного чемпиона. После смерти он сразу возрождается, но начинает быстро терять здоровье. Если союзник успевает принять участие в убийстве врага, он остается в живых.\n\n",E:"[E]\nПРОГРАММА ЛОЯЛЬНОСТИ\nРената выпускает две химтековые ракеты, которые дают щит союзникам, а также замедляют врагов и наносят им урон.\n\n",R:"[R]\nСМЕНА РУКОВОДСТВА\nРената выпускает волну химикатов, которая вызывает Бешенство у всех пораженных врагов.\n\n"}}},{name:"БелВет",description:"Лесник",info:{popular:"4%",win:"50,5%",block:"11,3%",skills:{passive:"ЛИЛОВАЯ СМЕРТЬ\nУчаствуя в убийствах чемпионов, больших миньонов и больших монстров, Бел'Вет получает заряды, которые навсегда повышают ее скорость атаки. Кроме того, ее скорость атаки ненадолго повышается после применения умений.\n\n",Q:"[Q]\nНЕОТВРАТИМАЯ БЕЗДНА\nБел'Вет совершает рывок в выбранном направлении и наносит урон всем врагам на своем пути.\n\n",W:"[W]\nСВЕРХУ И СНИЗУ\nБел'Вет бьет хвостом, нанося пораженным врагам урон, а также подбрасывая их и замедляя.\n\n",E:"[E]\nЯРОСТЬ ИМПЕРАТРИЦЫ\nБел'Вет замирает на месте и создает вокруг себя убийственный вихрь, который наносит урон врагу с самым низким уровнем здоровья. Вихрь дает ей прибавку к вампиризму и уменьшает получаемый урон.\n\n",R:"[R]\nНЕУТОЛИМЫЙ ГОЛОД\nБел'Вет поглощает коралл Бездны и принимает истинную форму, которая дает прибавки к максимальному запасу здоровья, дальности атаки, скорости атаки и скорости передвижения вне боя. Поглотив коралл Бездны, оставшийся после эпического монстра из Бездны, Бел'Вет продлевает действие умения и получает способность призывать ремор Бездны.\n\n"}}},{name:"Нила",description:"АДК",info:{popular:"4,1%",win:"51,3%",block:"10,4%",skills:{passive:"БЕСКОНЕЧНЫЙ ВОСТОРГ\nНила получает больше опыта за убийство миньонов. Кроме того, она усиливает эффекты лечения и щитов союзников поблизости.\n\n",Q:"[Q]\nХЛЕСТКИЙ КЛИНОК\nНила бьет хлыстом по прямой, нанося урон всем пораженным врагам. После этого у нее ненадолго увеличивается дальность атаки.\n\n",W:"[W]\nПОКРОВ РАДОСТИ\nНила укрывается туманом и ускоряется, а также начинает уклоняться ото всех вражеских автоатак. Если она касается союзников, этот эффект начинает действовать и на них.\n\n",E:"[E]\nНЫРОК\nНила совершает рывок сквозь выбранного бойца, нанося урон всем врагам на пути.\n\n",R:"[R]\nАПОФЕОЗ\nНила совершает круговой удар хлыстом, нанося урон всем врагам поблизости, после чего подтягивает их к себе.\n\n"}}},{name:"Атрокс",description:"Топер",info:{popular:"14,4%",win:"50,4%",block:"29%",skills:{passive:"СТОЙКА РАЗРУШИТЕЛЯ\nПериодически следующая автоатака Атрокса дополнительно наносит физический урон в зависимости от максимального запаса здоровья цели и лечит его.\n\n",Q:"[Q]\nКЛИНОК ДАРКИНОВ\nАтрокс бьет мечом сверху вниз, нанося физический урон. Он может ударить три раза, и каждый раз область поражения будет меняться.\n\n",W:"[W]\nПРОКЛЯТЫЕ ЦЕПИ\nАтрокс бьет по земле, нанося урон первому пораженному врагу. Если чемпионы или большие монстры не покинут область действия, то будут притянуты к ее центру и снова получат урон.\n\n",E:"[E]\nТЕМНЫЙ РЫВОК\nАтрокс пассивно восстанавливает себе здоровье при нанесении урона вражеским чемпионам. При активации он совершает рывок в выбранном направлении.\n\n",R:"[R]\nГУБИТЕЛЬ МИРА\nАтрокс принимает демоническую форму, пугая ближайших вражеских миньонов, а также увеличивая свою силу атаки, принимаемое лечение и скорость передвижения. Если он зарабатывает убийство или содействие, продолжительность эффекта увеличивается.\n\n"}}},{name:"Акали",description:"Мидер",info:{popular:"12,7%",win:"47,3%",block:"16,1%",skills:{passive:"МЕТКА УБИЙЦЫ\nПри нанесении урона умением чемпиону вокруг него появляется круг энергии. При выходе из круга Акали усиливает следующую автоатаку, увеличивая ее дальность и урон.\n\n",Q:"[Q]\nПЯТИТОЧЕЧНЫЙ УДАР\nАкали бросает пять кунаев, нанося урон в зависимости от дополнительной силы атаки и силы умений, а также замедляя цель.\n\n",W:"[W]\nПОКРОВ СУМЕРЕК\nАкали создает дымовую завесу и ненадолго увеличивает свою скорость передвижения. Находясь внутри, она становится невидимой, а враги не могут выбрать ее в качестве цели для умений или автоатак. При применении автоатак или умений Акали ненадолго раскрывает себя.\n\n",E:"[E]\nСЮРИКЕН УБИЙЦЫ\nАкали совершает сальто назад и бросает перед собой сюрикен, нанося магический урон и помечая первого пораженного врага или дымовую завесу. При повторном применении Акали совершает рывок к помеченной цели, нанося дополнительный урон.\n\n",R:"[R]\nИДЕАЛЬНОЕ УБИЙСТВО\nАкали совершает рывок в выбранном направлении, нанося урон пораженным врагам. При повторном применении Акали совершает еще один рывок в выбранном направлении, нанося урон в зависимости от недостающего здоровья цели.\n\n"}}},{name:"Ари",description:"Мидер",info:{popular:"4,8%",win:"50,3%",block:"1,9%",skills:{passive:"ПОХИЩЕНИЕ СУЩНОСТИ\nАри лечится, когда убивает 9 миньонов или монстров. Поучаствовав в убийстве чемпиона, она восстанавливает себе еще больше здоровья.\n\n",Q:"[Q]\nСФЕРА ОБМАНА\nАри запускает сферу, которая наносит магический урон всем врагам на своем пути, пока летит от Ари, и чистый урон по пути назад.\n\n",W:"[W]\nЛИСИЙ ОГОНЬ\nАри ненадолго ускоряется и выпускает три огонька, которые нацеливаются на ближайших врагов и атакуют их.\n\n",E:"[E]\nОЧАРОВАНИЕ\nАри посылает воздушный поцелуй, который наносит магический урон и очаровывает врага, мгновенно останавливая умения перемещения и заставляя его завороженно идти по направлению к ней.\n\n",R:"[R]\nПРИЗРАЧНЫЙ РЫВОК\nАри совершает рывок вперед и стреляет молниями душ, нанося урон врагам поблизости. Призрачный рывок можно использовать до 3 раз до перезарядки умения. Количество применений увеличивается, когда Ари участвует в убийстве вражеских чемпионов.\n\n"}}},{name:"Акшан",description:"Мидер",info:{popular:"3,4%",win:"51,3%",block:"3,7%",skills:{passive:"БОЙ БЕЗ ПРАВИЛ\nПри каждом третьем попадании автоатакой или умением по врагу Акшан наносит дополнительный урон, а также получает щит, если цель - чемпион. После каждой автоатаки Акшан стреляет еще раз, нанося меньше урона. Если он отменяет этот выстрел, его скорость передвижения увеличивается.\n\n",Q:"[Q]\nБУМЕРАНГ МСТИТЕЛЯ\nАкшан бросает бумеранг, который наносит урон, когда летит вперед и когда возвращается. Дальность полета увеличивается с каждой пораженной целью.\n\n",W:"[W]\nОПАСНАЯ ИГРА\nПассивно: когда вражеский чемпион убивает союзника Акшана, он становится негодяем. Если Акшан убивает негодяя, он воскрешает всех убитых им союзников и получает дополнительное золото. При этом остальные негодяи избавляются от этого статуса. Активно: Акшан маскируется, а также увеличивает свои скорость передвижения и восстановление маны при перемещении к негодяям. Акшан быстро теряет маскировку, если находится не в кустах и не около элемента ландшафта.\n\n",E:"[E]\nЗАЦЕПНОЙ ПИСТОЛЕТ\nАкшан выстреливает крюком в элемент ландшафта, а затем облетает его, непрерывно расстреливая ближайшего врага. Он может спрыгнуть раньше времени. При столкновении с чемпионом или элементом ландшафта Акшан спрыгивает автоматически.\n\n",R:"[R]\nРАСПЛАТА\nАкшан фиксируется на вражеском чемпионе и накапливает пули. Затем он начинает стрелять. Каждая пуля наносит первому пораженному чемпиону, миньону или строению урон, который зависит от недостающего здоровья цели.\n\n"}}},{name:"Амуму",description:"Топер",info:{popular:"5,1%",win:"50,6%",block:"4,8%",skills:{passive:"ПРИКОСНОВЕНИЕ МУМИИ\nАвтоатаки Амуму накладывают Проклятие на цель, из-за чего она получает дополнительный чистый урон при поражении магическим уроном.\n\n",Q:"[Q]\nБРОСОК БИНТА\nАмуму кидает во врага бинт. При попадании бинт приклеивается к врагу, оглушая его, и Амуму подтягивается к цели, нанося ей урон.\n\n",W:"[W]\nОТЧАЯНИЕ\nАмуму внушает отчаяние окружающим врагам, отчего они теряют долю от своего максимального запаса здоровья каждую секунду, а наложенное на них Проклятие обновляется.\n\n",E:"[E]\nВСПЫШКА ГНЕВА\nАмуму получает уменьшенный физический урон. При активации умения Амуму дает волю гневу, нанося урон всем окружающим врагам. Каждый раз, когда Амуму получает урон, перезарядка умения сокращается на 0.5 секунды.\n\n",R:"[R]\nПРОКЛЯТИЕ ПЕЧАЛЬНОЙ МУМИИ\nАмуму опутывает окружающих врагов бинтами, накладывая на них Проклятие, нанося урон и оглушая.\n\n"}}},{name:"Анивия",description:"Мидер",info:{popular:"3%",win:"51,1%",block:"2,6%",skills:{passive:"ПЕРЕРОЖДЕНИЕ\nПри получении смертельного урона Анивия превращается в яйцо, а затем перерождается с полным запасом здоровья.\n\n",Q:"[Q]\nМГНОВЕННАЯ ЗАМОРОЗКА\nАнивия создает сферу льда, которая летит к противникам, охлаждая и нанося урон каждому, кто попадется на ее пути. Когда сфера взрывается, она наносит урон по площади и оглушает всех в зоне поражения.\n\n",W:"[W]\nКРИСТАЛЛИЗАЦИЯ\nАнивия конденсирует влагу из воздуха и замораживает ее, создавая непроходимую ледяную стену. Спустя некоторое время стена тает.\n\n",E:"[E]\nОБМОРОЖЕНИЕ\nАнивия взмахивает крыльями и направляет порыв ледяного ветра в указанную цель, нанося урон. Если цель была недавно поражена Мгновенной заморозкой или получила урон от Ледяной бури максимального размера, урон удваивается.\n\n",R:"[R]\nЛЕДЯНАЯ БУРЯ\nАнивия призывает ледяную бурю, которая замедляет противников и наносит им урон.\n\n"}}},{name:"Энни",description:"Мидер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ПИРОМАНИЯ\nКаждое пятое умение Энни оглушает цель.\n\n",Q:"[Q]\nРАСЩЕПЛЕНИЕ\nЭнни швыряет во врага заряженный маной огненный шар, нанося ему магический урон. Если враг умирает, затраченная мана восстанавливается.\n\n",W:"[W]\nИСПЕПЕЛЕНИЕ\nЭнни создает конус из пламени, нанося урон всем врагам в зоне поражения.\n\n",E:"[E]\nПЫЛАЮЩИЙ ЩИТ\nНакладывает на Энни или ее союзника щит, кратковременно увеличивает скорость передвижения, а также наносит урон врагам, которые применяют автоатаки к цели со щитом.\n\n",R:"[R]\nПРИЗЫВ ТИББЕРСА\nЭнни призывает своего медведя Тибберса, который будет сражаться на ее стороне и поджигать ближайших врагов. В момент призыва все враги рядом с Тибберсом получают урон.\n\n"}}},{name:"Алистар",description:"Сапорт",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ТОРЖЕСТВУЮЩИЙ РЕВ\nАлистар готовит свой рев, оглушая или смещая вражеских чемпионов, а также находясь рядом с умирающими врагами. Заряженный рев восстанавливает здоровье Алистару и всем ближайшим союзным чемпионам.\n\n",Q:"[Q]\nСОКРУШЕНИЕ\nАлистар бьет по земле, нанося урон окружающим врагам и подбрасывая их в воздух.\n\n",W:"[W]\nУДАР ГОЛОВОЙ\nАлистар таранит цель, нанося урон и отбрасывая ее назад.\n\n",E:"[E]\nТОПОТ\nАлистар топчет ближайших врагов, получая возможность проходить сквозь бойцов и накапливая заряд за каждое повреждение вражеского чемпиона. При накоплении максимального количества зарядов следующая автоатака Алистара нанесет вражескому чемпиону дополнительный магический урон и оглушит его.\n\n",R:"[R]\nНЕСОКРУШИМАЯ ВОЛЯ\nАлистар издает неистовый рев, освобождаясь ото всех эффектов контроля, а также на некоторое время уменьшая входящий физический и магический урон.\n\n"}}},{name:"Афелий",description:"АДК",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"УБИЙЦА И ПРОВИДИЦА\nАфелий владеет 5 видами оружия Лунари, которые создала его сестра Алуна. Одновременно он использует два оружия: основное и дополнительное. У каждого оружия уникальные автоатаки и умение. Атаки и умения расходуют боеприпасы оружия. Когда они заканчиваются, Афелий выбрасывает текущее оружие и Алуна дает ему следующее в очереди из пяти.\n\n",Q:"[Q]\nОРУЖЕЙНЫЕ УМЕНИЯ\nУ Афелия есть 5 разных активируемых умений, которые зависят от основного оружия: Калибрум (винтовка): Афелий совершает выстрел с большого расстояния, помечая цель. После этого он может поразить помеченного врага еще раз с любого места на карте. Северум (пистолет-коса): Афелий ускоряется и расстреливает ближайших врагов из обоих видов оружия. Гравитум (пушка): Афелий обездвиживает всех врагов, замедленных этим оружием. Инфернум (огнемет): Афелий поражает врагов в конусе и атакует их с помощью дополнительного оружия. Кресцендум (шакрам): Афелий создает стража, который атакует врагов с помощью дополнительного оружия.\n\n",W:"[W]\nФАЗА\nАфелий меняет местами основное и дополнительное оружие, получая новые автоатаки и активируемое умение.\n\n",E:"[E]\nСИСТЕМА ОЧЕРЕДИ ВИДОВ ОРУЖИЯ\nУ Афелия нет третьего умения. В этой ячейке показывается следующее оружие, которое ему даст Алуна. Порядок получения оружия фиксированный, но может меняться по ходу игры. Когда заканчиваются боеприпасы для текущего оружия, оно переносится в конец очереди.\n\n",R:"[R]\nЛУННЫЙ ДОЗОР\nАфелий выпускает концентрированный сгусток лунного света, который взрывается при поражении вражеских чемпионов. Накладывает уникальный эффект основного оружия Афелия.\n\n"}}},{name:"Эш",description:"АДК",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"МОРОЗНЫЙ ВЫСТРЕЛ\nАвтоатаки Эш замедляют врагов и помечают их. Помеченным целям Эш наносит дополнительный урон. Критические удары Эш не наносят увеличенного урона, однако сильнее замедляют цель.\n\n",Q:"[Q]\nСОСРЕДОТОЧЕННОСТЬ СТРЕЛКА\nАвтоатаки Эш повышают Сосредоточенность. Когда накапливается максимальное количество зарядов, умение можно активировать. При этом все заряды Сосредоточенности поглощаются, и скорость атаки Эш на некоторое время увеличивается. Кроме того, в течение этого времени при каждой автоатаке Эш выпускает град стрел.\n\n",W:"[W]\nЗАЛП\nЭш выпускает веер из стрел, нанося увеличенный урон. Умение также накладывает эффект Морозного выстрела.\n\n",E:"[E]\nЯСТРЕБИНАЯ РАЗВЕДКА\nЭш отправляет дух ястреба на разведку в любую точку на карте.\n\n",R:"[R]\nЗАЧАРОВАННАЯ ЛЕДЯНАЯ СТРЕЛА\nЭш выпускает ледяную стрелу в указанном направлении. Если стрела попадает во вражеского чемпиона, она наносит урон и оглушает его на некоторое время (зависит от дальности полета стрелы). Окружающие цель противники тоже получают урон и замедляются.\n\n"}}},{name:"Аурелион Сол",description:"Мидер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ЦЕНТР ВСЕЛЕННОЙ\nВокруг Аурелиона Сола постоянно вращаются звезды, нанося магический урон задетым врагам.\n\n",Q:"[Q]\nРАСТУЩАЯ ЗВЕЗДА\nАурелион Сол запускает в выбранном направлении растущую звезду, которая взрывается, когда оказывается слишком далеко от него, нанося урон и оглушая всех ближайших врагов.\n\n",W:"[W]\nЗВЕЗДНАЯ ЭКСПАНСИЯ\nАурелион Сол отправляет свои звезды на Внешнюю орбиту, увеличивая их урон и скорость.\n\n",E:"[E]\nЛЕГЕНДАРНАЯ КОМЕТА\nАурелион Сол взлетает и пролетает большое расстояние.\n\n",R:"[R]\nГОЛОС СВЕТА\nАурелион Сол выпускает в выбранном направлении волну звездного огня, нанося урон всем пораженным врагам и замедляя их. Кроме того, находящиеся поблизости враги отбрасываются.\n\n"}}},{name:"Азир",description:"Мидер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"НАСЛЕДИЕ ШУРИМЫ\nАзир может установить солнечный диск на развалинах союзной или вражеской башни.\n\n",Q:"[Q]\nНЕУМОЛИМЫЕ ПЕСКИ\nАзир отдает приказ всем песчаным воинам сделать рывок в указанную точку. Воины наносят магический урон врагам на своем пути и замедляют их на 1 секунду.\n\n",W:"[W]\nВОССТАНЬ!\nПри активации умения Азир призывает песчаного воина, который автоатакует вместо него в пределах своей дальности атаки. Автоатаки песчаных воинов наносят магический урон всем врагам на прямой линии. При этом Азир и его песчаные воины получают пассивную прибавку к скорости атаки.\n\n",E:"[E]\nПОДВИЖНЫЙ ПЕСОК\nАзир ненадолго защищает себя щитом и совершает рывок к одному из своих песчаных воинов, нанося урон врагам на своем пути. Если Азир сталкивается с вражеским чемпионом, у него мгновенно появляется в запасе еще один воин, а рывок прекращается.\n\n",R:"[R]\nРАЗДЕЛЯЙ И ВЛАСТВУЙ\nАзир призывает фалангу неуправляемых воинов, которые устремляются вперед, отбрасывая врагов и нанося им урон.\n\n"}}},{name:"Бард",description:"Сапорт",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ЗОВ СКИТАЛЬЦА\nМипы: Бард притягивает маленьких духов, которые помогают ему при автоатаках, нанося дополнительный магический урон. Когда Бард собирает достаточно колокольчиков, его мипы начинают наносить урон по площади и замедлять пораженных врагов. Колокольчики: в случайных местах на карте появляются древние колокольчики, которые Бард может собирать. Они дают опыт, восстанавливают ману и увеличивают скорость передвижения вне боя.\n\n",Q:"[Q]\nОКОВЫ ВСЕЛЕННОЙ\nБард выпускает в выбранном направлении энергетический заряд, который поражает до двух врагов на своем пути, нанося им урон. Первая пораженная цель замедляется. Если после поражения первой цели заряд попадает во врага или стену, все пораженные цели оглушаются.\n\n",W:"[W]\nАЛТАРЬ ХРАНИТЕЛЯ\nБард призывает древний артефакт, который набирает силу в течение некоторого времени. Артефакт восстанавливает здоровье и ненадолго увеличивает скорость передвижения первому союзному чемпиону, который его коснется, перед тем, как исчезнуть.\n\n",E:"[E]\nВОЛШЕБНОЕ ПУТЕШЕСТВИЕ\nБард создает портал, позволяющий пройти сквозь выбранное препятствие. Порталом могут воспользоваться как союзные, так и вражеские чемпионы, но только в одном направлении.\n\n",R:"[R]\nОТСРОЧКА НЕИЗБЕЖНОГО\nБард посылает энергию духов в указанную область, на короткое время помещая всех бойцов и башни в ней в стазис.\n\n"}}},{name:"Блицкранк",description:"Сапорт",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ЩИТ МАНЫ\nКогда у Блицкранка остается мало здоровья, он получает щит, прочность которого зависит от его запаса маны.\n\n",Q:"[Q]\nРАКЕТНЫЙ ЗАХВАТ\nБлицкранк выстреливает правой рукой, притягивая первого пораженного врага к себе и нанося ему урон.\n\n",W:"[W]\nУСКОРЕНИЕ\nБлицкранк перегружает себя, значительно увеличивая свои скорость передвижения и скорость атаки. По окончании перегрузки его скорость передвижения на время уменьшается.\n\n",E:"[E]\nСИЛОВОЙ КУЛАК\nБлицкранк заряжает кулак энергией, чтобы нанести двойной урон и подбросить противника в воздух при следующей атаке.\n\n",R:"[R]\nСТАТИЧЕСКОЕ ПОЛЕ\nВраги, которых атакует Блицкранк, помечаются и через 1 секунду поражаются молнией, которая наносит урон. Умение можно активировать, чтобы уничтожить щиты ближайших врагов, нанести им урон и ненадолго наложить безмолвие.\n\n"}}},{name:"Брэнд",description:"Сапорт",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ПОДЖОГ\nУмения Брэнда поджигают врагов, в течение 4 секунд нанося им урон (накапливается до 3 зарядов). Если Брэнд убивает подожженного врага, он восстанавливает себе ману. Достигая максимального количества зарядов, Поджог чемпиона или большого монстра становится нестабильным. Он взрывается через 2 секунды, накладывая эффекты заклинания и нанося огромный урон в области вокруг жертвы.\n\n",Q:"[Q]\nВЫГОРАНИЕ\nБрэнд выпускает в указанном направлении огненный шар, который наносит магический урон. Если цель в огне, Выгорание оглушает ее на 1.5 секунды.\n\n",W:"[W]\nСТОЛБ ПЛАМЕНИ\nПосле небольшой задержки Брэнд создает в указанной точке огненный столб, который наносит магический урон всем врагам в зоне поражения. Если цель в огне, Столб пламени наносит ей на 25% урона больше.\n\n",E:"[E]\nВОСПЛАМЕНЕНИЕ\nБрэнд воспламеняет цель, после чего пламя распространяется на ближайших врагов, нанося магический урон. Если цель в огне, дальность распространения Воспламенения увеличивается вдвое.\n\n",R:"[R]\nОЧИЩЕНИЕ ОГНЕМ\nБрэнд высвобождает разрушительный поток огня, который перекидывается между Брэндом и врагами поблизости до 5 раз. При каждом поражении враги получают магический урон. Очищение огнем старается перекинуться на чемпионов для увеличения количества зарядов Поджога. Если цель в огне, Очищение огнем ненадолго замедляет ее.\n\n"}}},{name:"Браум",description:"Сапорт",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ОГЛУШАЮЩИЕ УДАРЫ\nАвтоатаки Браума накладывают на врагов заряды Оглушающих ударов. После появления первого заряда на цели автоатаки союзников начинают накладывать на нее дополнительные заряды. Цель, получившая 4 заряда, получает магический урон и оглушается. В течение следующих нескольких секунд цель не может получить новые заряды Оглушающих ударов, но получает дополнительный магический урон от автоатак Браума.\n\n",Q:"[Q]\nУКУС ЗИМЫ\nБраум стряхивает лед со своего щита, замедляя пораженного врага и нанося ему магический урон. Умение накладывает заряд Оглушающих ударов.\n\n",W:"[W]\nПРЯЧЬСЯ ЗА МЕНЯ\nБраум бросается на помощь союзному чемпиону или миньону. Оказавшись рядом с ним, Браум и его союзник получают дополнительную броню и сопротивление магии на несколько секунд.\n\n",E:"[E]\nНЕУЯЗВИМЫЙ\nБраум поднимает щит и уменьшает урон от всех атак с определенного направления. Первая атака блокируется целиком, остальные наносят лишь часть урона все то время, пока щит поднят. Браум также перехватывает все выстрелы - они наносят ему урон и разрушаются.\n\n",R:"[R]\nЛЕДНИКОВЫЙ РАЗЛОМ\nБраум бьет щитом по земле, создавая мощный и дальнобойный направленный удар. Пораженные враги подбрасываются в воздух, получают урон и замедляются.\n\n"}}},{name:"Кейтлин",description:"АДК",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ВЫСТРЕЛ В ГОЛОВУ\nКаждые несколько автоатак или при атаке пойманных в ловушку или сеть врагов Кейтлин стреляет противнику в голову, нанося дополнительный урон, зависящий от шанса критического удара. При стрельбе по пойманным в ловушку или сеть врагам дальность автоатаки увеличивается вдвое.\n\n",Q:"[Q]\nПИЛТОВЕРСКИЙ МИРОТВОРЕЦ\nКейтлин подстраивает свою винтовку в течение секунды, а затем делает выстрел, который пробивает врагов насквозь, нанося всем пораженным целям физический урон (каждая следующая цель получает меньший урон).\n\n",W:"[W]\nЛОВУШКА НА ЙОРДЛОВ\nКейтлин устанавливает ловушку. После срабатывания она раскрывает и обездвиживает вражеского чемпиона на 1.5 сек., а также позволяет Кейтлин применить усиленный Выстрел в голову.\n\n",E:"[E]\nСЕТЬ 90 КАЛИБРА\nКейтлин выстреливает тяжелой сетью, замедляя цель. Отдача отбрасывает ее назад.\n\n",R:"[R]\nГЛАВНЫЙ КОЗЫРЬ\nКейтлин тщательно прицеливается и делает превосходный выстрел, наносящий огромный урон одиночной цели на гигантском расстоянии. Вражеские чемпионы могут заслонить собой своего союзника.\n\n"}}},{name:"Камила",description:"Топер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"АДАПТИВНАЯ ЗАЩИТА\nПри поражении чемпиона автоатакой Камилла ненадолго получает щит против урона, соответствующего основному типу урона пораженной цели (физическому или магическому). Прочность щита зависит от максимального запаса здоровья Камиллы.\n\n",Q:"[Q]\nПРОТОКОЛ ТОЧНОСТИ\nСледующая автоатака Камиллы наносит дополнительный урон и увеличивает ее скорость передвижения. В течение короткого времени это умение можно использовать снова. Урон от второго удара увеличивается, если Камилла нанесет второй удар с задержкой.\n\n",W:"[W]\nТАКТИЧЕСКИЙ ВЗМАХ\nКамилла с небольшой задержкой наносит удар по врагам, находящимся перед собой. Враги в дальней половине поражаемой области замедляются, получают дополнительный урон и восстанавливают Камилле здоровье.\n\n",E:"[E]\nВЫСТРЕЛ КРЮКОМ\nКамилла притягивает себя к препятствию, отпрыгивает от него и по приземлении подбрасывает вражеских чемпионов.\n\n",R:"[R]\nХЕКСТЕКОВЫЙ УЛЬТИМАТУМ\nКамилла совершает рывок к выбранному чемпиону и запирает его внутри барьера. Кроме того, ее автоатаки дополнительно наносят магический урон выбранной цели.\n\n"}}},{name:"Кассиопея",description:"Мидер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ЗМЕИНАЯ ГРАЦИЯ\nПри получении каждого нового уровня скорость передвижения Кассиопеи увеличивается, но она не может покупать ботинки.\n\n",Q:"[Q]\nЯДОВИТЫЙ ВЗРЫВ\nКассиопея поражает указанную область сильнодействующим ядом, наносящим урон после небольшой задержки. Если был задет вражеский чемпион, скорость передвижения Кассиопеи увеличивается.\n\n",W:"[W]\nМИАЗМЫ\nКассиопея выпускает несколько облаков яда, которые наносят небольшой урон, замедляют и парализуют врагов, проходящих сквозь них. Парализованные враги не могут использовать умения перемещения.\n\n",E:"[E]\nДВА КЛЫКА\nКассиопея запускает во врага клыки, нанося увеличенный урон отравленным врагам и восстанавливая здоровье на величину, равную доле от нанесенного урона. Если цель умирает, Кассиопея получает обратно затраченную ману.\n\n",R:"[R]\nКАМЕНЯЩИЙ ВЗГЛЯД\nКассиопея выпускает из глаз поток магической энергии, оглушая всех врагов перед собой, обращенных к ней лицом, и замедляя обращенных к ней спиной.\n\n"}}},{name:"Чо`гат",description:"Топер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ПЛОТОЯДНОСТЬ\nКаждый раз, когда Чо'Гат убивает бойца, он восстанавливает себе здоровье и ману. Величина восстановления растет с уровнем Чо'Гата.\n\n",Q:"[Q]\nПРОЛОМ\nПронзает землю в указанной области, подбрасывая в воздух врагов и замедляя их.\n\n",W:"[W]\nДИКИЙ КРИК\nЧо`Гат издает ужасающий крик, который наносит находящимся перед ним врагам магический урон и заставляет их замолчать на несколько секунд.\n\n",E:"[E]\nСМЕРТОНОСНЫЕ ШИПЫ\nПри каждой автоатаке Чо'Гат выпускает смертоносные шипы, наносящие урон всем врагам перед ним и замедляя их.\n\n",R:"[R]\nПОЖИРАНИЕ\nЧо`Гат пожирает врага, нанося ему большое количество чистого урона. Если цель умирает, Чо'Гат растет, получая прибавку к максимальному запасу здоровья.\n\n"}}},{name:"Ясуо",description:"Мидер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ПУТЬ СКИТАЛЬЦА\nШанс критического удара Ясуо увеличен. Кроме того, передвигаясь, Ясуо накапливает поток для создания щита. Щит срабатывает при получении урона от чемпиона или монстра.\n\n",Q:"[Q]\nСТАЛЬНАЯ БУРЯ\nЯсуо делает выпад вперед, нанося урон всем врагам на прямой линии. При попадании создает один заряд Надвигающегося шторма на несколько секунд. После создания 2 зарядов Стальная буря выпускает вихрь, который подбрасывает врагов. Стальная буря приравнивается к автоатаке и масштабируется так же, как и автоатака.\n\n",W:"[W]\nСТЕНА ВЕТРА\nЯсуо создает движущуюся стену, которая блокирует все выстрелы врагов в течение 4 сек.\n\n",E:"[E]\nСТРЕМИТЕЛЬНЫЙ КЛИНОК\nЯсуо совершает рывок сквозь цель, нанося магический урон. Каждый рывок увеличивает базовый урон следующего (но не более чем на максимальное значение). Нельзя использовать против одного и того же врага в течение нескольких секунд. Если во время рывка использовать Стальную бурю, она ударит по кругу.\n\n",R:"[R]\nПОСЛЕДНИЙ ВЗДОХ\nЯсуо телепортируется к подброшенному вражескому чемпиону, нанося физический урон и удерживая в воздухе всех подброшенных рядом врагов. При этом его запас потока увеличивается до максимального, но все заряды Надвигающегося шторма обнуляются. Кроме того, в течение некоторого времени после этого критические удары Ясуо игнорируют значительную часть дополнительной брони врага.\n\n"}}},{name:"Енэ",description:"Мидер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ПУТЬ ОХОТНИКА\nКаждая вторая автоатака Ёнэ наносит магический урон. Кроме того, у него увеличен шанс критического удара.\n\n",Q:"[Q]\nСТАЛЬ СМЕРТНОГО\nЁнэ делает выпад вперед, нанося урон всем врагам на прямой линии. При попадании Ёнэ получает заряд Надвигающегося шторма на несколько секунд. После накопления 2 зарядов Ёнэ при использовании Стали смертного совершает рывок вперед с порывом ветра, подбрасывая врагов. Сталь смертного приравнивается к автоатаке и масштабируется так же, как и автоатака.\n\n",W:"[W]\nРАССЕЧЕНИЕ ДУХА\nЁнэ совершает удар, нанося урон всем врагам в конусе перед собой. При этом Ёнэ получает щит, прочность которого зависит от количества пораженных чемпионов. Время перезарядки и время произнесения Рассечения духа зависят от скорости атаки.\n\n",E:"[E]\nОСВОБОЖДЕННАЯ ДУША\nЁнэ покидает тело в форме духа и увеличивает свою скорость передвижения. По окончании действия умения дух Ёнэ возвращается в тело и снова наносит часть урона, нанесенного перед этим.\n\n",R:"[R]\nРЕШЕНИЕ СУДЬБЫ\nЁнэ телепортируется за последнего чемпиона на прямой, поражая всех врагов на пути с такой силой, что они подтягиваются к нему.\n\n"}}},{name:"Шая",description:"АДК",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ЧИСТЫЕ ПОРЕЗЫ\nПосле использования умения следующие автоатаки Шаи поражают всех врагов на пути и оставляют после себя перья.\n\n",Q:"[Q]\nПАРНЫЕ КИНЖАЛЫ\nШая бросает два кинжала, наносящих урон, и оставляет перья, которые она может призвать к себе.\n\n",W:"[W]\nСМЕРТОНОСНЫЕ ПЕРЬЯ\nШая создает вихрь из клинков, который увеличивает ее скорость атаки и урон автоатак, а также увеличивает ее скорость передвижения при атаке чемпиона.\n\n",E:"[E]\nЗОВ КЛИНКОВ\nШая призывает к себе все сброшенные перья, нанося урон пораженным врагам и обездвиживая их.\n\n",R:"[R]\nПЕРЬЕВАЯ БУРЯ\nШая взмывает в воздух, становясь недоступной для выбора в качестве цели, и швыряет веер кинжалов, оставляющих после себя перья, которые она может призвать к себе.\n\n"}}},{name:"Рейкан",description:"Сапорт",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ШАЛЬНЫЕ ПЕРЬЯ\nВокруг Рэйкана периодически появляется щит.\n\n",Q:"[Q]\nБЛЕСТЯЩЕЕ ПЕРО\nРэйкан бросает волшебное перо, которое наносит магический урон. При поражении чемпиона или эпического монстра Рэйкан может излечить своих союзников.\n\n",W:"[W]\nВЕЛИКОЛЕПНОЕ ПОЯВЛЕНИЕ\n\nРэйкан делает рывок в выбранное место, после чего подбрасывает ближайших врагов.\n\n",E:"[E]\nБОЕВОЙ ТАНЕЦ\nРэйкан летит к союзному чемпиону, защищая его щитом. В течение короткого промежутка времени умение можно использовать еще раз без затрат маны.\n\n",R:"[R]\nСТРЕМИТЕЛЬНОСТЬ\nСкорость передвижения Рэйкана увеличивается, и он очаровывает всех врагов, которых касается, и наносит им магический урон.\n\n"}}},{name:"Гвен",description:"Топер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ТЫСЯЧА ПОРЕЗОВ\nАвтоатаки Гвен наносят дополнительный магический урон в зависимости от запаса здоровья цели. Гвен восстанавливает себе здоровье в размере части урона, нанесенного чемпионам с помощью этого эффекта.\n\n",Q:"[Q]\nЧИК-ЧИК!\nГвен щелкает ножницами в конусе перед собой до 6 раз, нанося магический урон. Бойцы в центре области поражения получают чистый урон, и каждый разрез накладывает на них эффект пассивного умения.\n\n",W:"[W]\nСВЯЩЕННЫЙ ТУМАН\nГвен создает область из тумана, которая защищает ее от врагов извне. Выбрать ее целью могут лишь враги, которые находятся в тумане.\n\n",E:"[E]\nСВОБОДНЫЙ КРОЙ\nГвен совершает рывок на небольшое расстояние и усиливает свои автоатаки на несколько секунд: их дальность и скорость увеличиваются, и они наносят магический урон при попадании. Если она поражает врага в течение этого времени, перезарядка умения частично сокращается.\n\n",R:"[R]\nВЫШИВАНИЕ\nГвен выпускает перед собой иголку, которая замедляет врагов, наносит магический урон и накладывает на пораженных чемпионов эффект Тысячи порезов. Это умение можно применить еще два раза - при каждой повторной активации Гвен выпускает больше иголок и наносит увеличенный урон. Чтобы получить возможность повторно применить умение, после активации Гвен нужно поразить врага.\n\n"}}},{name:"Виего",description:"Лесник",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ПРАВО СИЛЬНОГО\nВраги, погибшие около Виего, превращаются в призраков. Атаковав призрака, Виего на время берет под контроль тело соответствующего врага. При этом он лечится на процент от максимального запаса здоровья цели, получает доступ к ее базовым умениям и предметам и может бесплатно применить свое абсолютное умение.\n\n",Q:"[Q]\nКЛИНОК ПАДШЕГО КОРОЛЯ\nПризрачный клинок Виего всегда наносит дополнительный урон при попадании, который зависит от текущего запаса здоровья цели. Кроме того, когда Виего атакует врага, который недавно был поражен его умением, он бьет дважды и похищает его здоровье. Виего может активировать это умение, совершив выпад с мечом и пронзив врагов перед собой.\n\n",W:"[W]\nПРИЗРАЧНАЯ ПАСТЬ\nПосле подготовки Виего совершает рывок, выпуская сгусток черного тумана, который оглушает первого пораженного врага.\n\n",E:"[E]\nПУТЬ СТРАХА\nВиего приказывает черному туману окружить элемент ландшафта. Когда Виего прячется в тумане, он становится призраком, получая маскировку, а также увеличивая скорость передвижения и скорость атаки.\n\n",R:"[R]\nРАЗБИТОЕ СЕРДЦЕ\nВиего телепортируется в выбранное место неподалеку и поражает вражеского чемпиона, пронзая его сердце и создавая вокруг разрушительную волну, которая отбрасывает остальных врагов поблизости.\n\n"}}},{name:"Векс",description:"Мидер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ТЛЕН И БЕЗЫСХОДНОСТЬ\nВекс периодически усиливается, и ее следующее базовое умение пугает врагов и прерывает рывки. Когда враги рядом с Векс совершают рывки, она помечает их, а при снятии метки наносит дополнительный урон и сокращает перезарядку своего усиления.\n\n",Q:"[Q]\nЧЕРНАЯ ПОЛОСА\nВекс выпускает наносящий урон снаряд, который ускоряется посреди полета.\n\n",W:"[W]\nЛИЧНОЕ ПРОСТРАНСТВО\nВекс получает щит и наносит урон врагам поблизости.\n\n",E:"[E]\nНЕИЗБЕЖНАЯ ТЬМА\nВекс создает область, которая наносит урон и замедляет врагов. Кроме того, она помечает их Безысходностью.\n\n",R:"[R]\nСТРЕМИТЕЛЬНАЯ ТЕНЬ\nВекс выпускает снаряд, который помечает вражеского чемпиона. При повторном применении умения Векс совершает рывок к цели и наносит урон.\n\n"}}},{name:"Катарина",description:"Мидер",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"НЕНАСЫТНОСТЬ\nОставшееся время перезарядки умений Катарины значительно сокращается каждый раз, когда умирает вражеский чемпион, которому она недавно нанесла урон. Когда Катарина подбирает кинжал, она производит круговую атаку, наносящую магический урон.\n\n",Q:"[Q]\nТАНЦУЮЩИЙ КЛИНОК\nКатарина мечет во врага кинжал, который рикошетом поражает еще несколько целей, после чего падает на землю.\n\n",W:"[W]\nПОДГОТОВКА\nКатарина подбрасывает над собой кинжал, ненадолго увеличивая свою скорость передвижения.\n\n",E:"[E]\nШУНПО\nКатарина телепортируется к цели, нанося урон этой цели, если ей окажется враг, или ближайшему врагу.\n\n",R:"[R]\nЦВЕТОК СМЕРТИ\nКатарина становится вихрем клинков и наносит огромный магический урон трем ближайшим вражеским чемпионам.\n\n"}}},{name:"Люкс",description:"Сапорт",info:{popular:"1,4%",win:"49,7%",block:"0,4%",skills:{passive:"ИЛЛЮМИНАЦИЯ\nИспользуя атакующие умения, Люкс накладывает на врагов эффект Иллюминации на несколько секунд. Ее последующие автоатаки поджигают находящихся под действием эффекта врагов, нанося им дополнительный магический урон (тем больший, чем выше уровень Люкс), но снимают эффект.\n\n",Q:"[Q]\nСКОВЫВАНИЕ СВЕТА\nЛюкс выпускает сферу света, которая наносит урон и обездвиживает до двух врагов.\n\n",W:"[W]\nПРИЗМАТИЧЕСКИЙ БАРЬЕР\nЛюкс бросает свой жезл, который, касаясь союзных чемпионов, искривляет вокруг них лучи света, защищая от входящего урона.\n\n",E:"[E]\nСВЕТЯЩАЯСЯ СИНГУЛЯРНОСТЬ\nЛюкс создает в указанной области световую аномалию, замедляющую противников. Люкс может взорвать ее, нанеся урон всем находящимся в зоне поражения врагам.\n\n",R:"[R]\nПОСЛЕДНЯЯ ИСКРА\nЛюкс накапливает энергию и выпускает мощный луч света, наносящий урон всем врагам на своем пути. Кроме того, луч поджигает врагов, на которых наложен эффект Иллюминации, после чего этот эффект обновляется.\n\n"}}},{name:"Ксантер",description:"Топер",info:{popular:"9,9%",win:"49,1%",block:"17,1%",skills:{passive:"БЕССТРАШИЕ\nУмения К'Санте помечают цели, увеличивая урон от его следующей автоатаки.\n\n",Q:"[Q]\nУДАРЫ НТОФО\nК'Санте бьет своим оружием, нанося урон всем врагам перед собой, а также замедляя их. Поразив цель, он получает заряд, который действует несколько секунд. Когда у К'Санте 2 заряда, это умение выпускает волну, которая притягивает врагов.\n\n",W:"[W]\nНАПРОЛОМ\nК'Санте совершает рывок после небольшой подготовки, во время которой он неудержим и получает меньше урона. Все пораженные цели оказываются оглушены и отброшены к конечной точке.\n\n",E:"[E]\nУДАЛЬ\nК'Санте совершает рывок к союзнику, после чего они оба получают щит. Если союзника нет, К'Санте все равно совершает рывок в выбранное место и получает щит. Дальность такого рывка уменьшена.\n\n",R:"[R]\nВЫСВОБОЖДЕНИЕ\n\nК'Санте совершает мощную атаку, отталкивая цель. Если враг попадает в стену, он перелетает ее и получает дополнительный урон. Затем К'Санте появляется за целью и высвобождает клинки, уменьшая свою защиту, но значительно увеличивая урон.\n\n"}}}]}},created(){},mounted(){this.champion=this.champions.find((n=>n.name===this.$route.query.name))},methods:{}},e=p,a=s(1001),c=(0,a.Z)(e,o,l,!1,null,"efecf358",null),t=c.exports}}]);
